import wx
import cStringIO
import base64


class SplashCrashApp(wx.App):
    def OnInit(self):
        wx.InitAllImageHandlers()
        
        splashimage = wx.ImageFromStream(cStringIO.StringIO(ExampleSplash))
        # If the splash image has alpha, it shows up transparently on
        # windows, so we blend it into a white background.
        splashbitmap = wx.EmptyBitmapRGBA(splashimage.GetWidth(), splashimage.GetHeight(), 255, 255, 255, 255)
        dc = wx.MemoryDC()
        dc.SelectObject(splashbitmap)
        dc.DrawBitmap(wx.BitmapFromImage(splashimage), 0, 0)
        ### Crash - comment out next line
        dc.Destroy() # necessary to avoid a crash in splashscreen
        self.splash = wx.SplashScreen(splashbitmap, wx.SPLASH_CENTRE_ON_SCREEN | wx.SPLASH_TIMEOUT, 2000, None, -1)

        self.new_version_check()

        self.frame = wx.Frame(None, -1, "Cell Profiler")

        self.SetTopWindow(self.frame)
        self.frame.Show()
        return 1

    def new_version_check(self):
        def cb2():
            # showing a modal dialog while the splashscreen is up causes a hang
            # Hang - comment out next two lines
            try: self.splash.Destroy()
            except: pass

            dlg = wx.MessageDialog(None, 'modal dialog', 'modal dialog', wx.OK)
            dlg.ShowModal()
            dlg.Destroy()

        wx.CallAfter(cb2)

ExampleSplash = base64.b64decode("")


if __name__ == "__main__":
    SplashCrashApp = SplashCrashApp(0)
    SplashCrashApp.MainLoop()
