static wxBitmap *abc_init(void)
{
	wxBitmap *bmp = new wxBitmap(100, 100, wxBITMAP_SCREEN_DEPTH);
	wxMemoryDC dc(*bmp);
	dc.SetPen(*wxBLACK_PEN);
	dc.SetBrush(*wxWHITE_BRUSH);
	dc.DrawRectangle(0, 0, 100, 300);
	dc.DrawBitmap(*bin2c_a_png, 0, 100-15-52);
	dc.DrawBitmap(*bin2c_b_png, 12, 100-26-54);
	dc.DrawBitmap(*bin2c_c_png, 40, 100-0-51);
	dc.SelectObject(wxNullBitmap);
	return bmp;
}

static wxBitmap *abc;

bool myApp::OnInit(void)
{
	wxImage::AddHandler(new wxPNGHandler());
	bin2c_init_IMG_HPP();
	abc = abc_init();
	auto spl = new wxSplashScreen(*abc, wxSPLASH_CENTRE_ON_SCREEN | wxSPLASH_NO_TIMEOUT, 0, NULL, wxID_ANY);
	wxYield();
	sleep(3); /* hard work */
	spl->Destroy();

	auto dlg = new wxDialog(NULL, wxID_ANY, "Test");
	auto vp = new wxBoxSizer(wxVERTICAL);
	vp->Add(new wxStaticBitmap(dlg, wxID_ANY, *abc));
	dlg->SetSizer(vp);
	vp->SetSizeHints(dlg);
	dlg->ShowModal();
	dlg->Destroy();
	return true;
}
