static wxBitmap *pv_banner_init(void)
{
	wxBitmap *bmp = new wxBitmap(500, 120, wxBITMAP_SCREEN_DEPTH);
	wxMemoryDC dc(*bmp);
	dc.SetPen(*wxBLACK_PEN);
	dc.SetBrush(*wxWHITE_BRUSH);
	dc.DrawRectangle(0, 0, 500, 120);
	dc.DrawBitmap(*deb3d_png, 30, 120 - 32 - 90);
#if 1 /* MAKE BACKGROUND PIC DISAPPEAR */
	dc.DrawBitmap(*splash_pv_png, 30, 120 - 1 - 26 - 1);
#endif
	dc.SelectObject(wxNullBitmap);
	return bmp;
}
bool pvApp::OnInit(void)
{
	init_icons();
	auto bmp = pv_banner_init();
	wxDialog *dlg = new wxDialog(NULL, wxID_ANY, "");
	auto vp = new wxBoxSizer(wxVERTICAL);
	vp->Add(new wxStaticBitmap(dlg, wxID_ANY, *bmp));
	dlg->SetSizer(vp);
	vp->SetSizeHints(dlg);
	dlg->ShowModal();
	return true;
}
